import React, { useState, useContext } from "react";

import Card from "../../shared/components/UIElements/Card";
import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import {
    VALIDATOR_EMAIL, VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE
} from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hook";
import { AuthContext } from "../../shared/context/auth-context";

import './Auth.css';

const Auth = () => {
    const auth = useContext(AuthContext);
    const [isLoginMode, setIsLoginMode] = useState();
    const [formState, InputHandler, setFormData] = useForm({
        email: {
            value: '',
            isValid: false
        },
        password: {
            value: '',
            isValid: false
        }
    }, false);

    const switchModeHandler = () => {
        if (!isLoginMode) {
            setFormData({
                ...formState.inputs,    // 기존의 값은 유지하며 
                name: undefined         // name 필드만 undefined함
            }, formState.inputs.email.isValid &&
            formState.inputs.password.isValid);
        } else {
            setFormData({
                ...formState.inputs,
                name: {
                    value: '',
                    isValid: false
                }
            }, false);
        }
        setIsLoginMode(prevMode => !prevMode);
    };

    const authSubmitHander = async event => {
        event.preventDefault(); // 폼 제출 시 기본 동작인 페이지 새로고침을 막고, 다른 작업을 수행할 수 있습니다.
        // fetch 함수를 쓰는 경우
        fetch('http://localhost:5000/api/users/signup');

        // axios 서드파티Lib 를 쓰는 경우
        try {
            // formState.inputs를 통해 폼 데이터를 가져옵니다.
            const response = await axios.post('http://localhost:5000/api/users/signup', {
                // 여기에 폼 데이터를 추가합니다.
                // 예를 들어, formState.inputs가 { email: 'test@test.com', password: '123456' }이라면:
                email: formState.inputs.email,
                password: formState.inputs.password
            });

            console.log(response.data); // 서버로부터의 응답 데이터를 출력합니다.
            auth.login(); // 로그인 함수 호출
        } catch (error) {
            console.error('Error during signup:', error); // 에러가 발생한 경우 에러를 출력합니다.
        }

        console.log(formState.inputs)
        auth.login();
    };

    return (<Card>
        <h2>Login Required</h2>
        <hr />
        <form className="authentication" onSubmit={authSubmitHander}>
            {!isLoginMode && (
                <Input
                    element="input"
                    id="name"
                    type="text"
                    label="Your Name"
                    validators={[VALIDATOR_REQUIRE()]}
                    errorText="Please enter a name."
                    onInput={InputHandler}
                />
            )}
            <Input element="input"
                id="email"
                type="email"
                label="E-mail"
                validators={[VALIDATOR_EMAIL()]}
                errorText="Please enter a valid Email address."
                onInput={InputHandler}
            />
            <Input element="input"
                id="password"
                type="password"
                label="Password"
                validators={[VALIDATOR_MINLENGTH(5)]}
                errorText="Please enter a valid password, at least 5 characters."
                onInput={InputHandler}
            />
            <Button type="submit" disabled={!formState.isValid}>
                {isLoginMode ? "확인" : "회원정보 작성 완료"}
            </Button>
        </form>
        <Button inverse onClick={switchModeHandler}>{isLoginMode ? '회원 가입' : '로그인'}으로 가기</Button>
    </Card>);
};

export default Auth;
